// addTodo.js
import React, { Component } from 'react';
import {
	View,
	TouchableOpacity,
	Text,
	TextInput,
	StyleSheet,
	Keyboard,
} from 'react-native';

import {connect} from 'react-redux';
import { addTodo } from '../redux/actions'

/**
 * a textInput and a button for user to add a todo item
 *
 * @reactProps {function} addTodo - the action when user clicks on the add button
 */

class AddTodo extends Component {
	constructor(props) {
		super(props)
		this.state = {
			todo: undefined
		}
	}

	render() {
		return (
			<View style={styles.container}>
				<TextInput
					style={styles.textInput}
					onChangeText={text => this.setState({todo: text})}
					clearButtonMode={'while-editing'}
				/>
				<TouchableOpacity
					style={styles.button}
				  onPress={() =>{
						Keyboard.dismiss()
						this.props.addTodo(this.state.todo)
					}} 
				>
					<Text> Add </Text>
				</TouchableOpacity>
			</View>
		)
	}
}


const styles = StyleSheet.create({
	container: {
		flexDirection: 'row',
		paddingTop: 20,
		height: 50,
	},
	textInput: {
		width: 200,
		borderWidth: 1,
	},
	button: {
		alignItems: 'center',
		justifyContent: 'center',
		marginLeft: 10
	}
})

AddTodo = connect(null, { addTodo })(AddTodo)

export default AddTodo

// todo.js
import React, { Component } from 'react';
import {
	View,
	TouchableOpacity,
	Text,
	StyleSheet,
} from 'react-native';


/**
 * The todo items.
 * @reactProps {!boolean} completed - the status of the todo item
 * @reactProps {!function} onPress - action when press on the todo item
 * @reactProps {!string} text - the content of the todo item
 */
export default class Todo extends Component {
	render() {
		const { completed, onPress, text } = this.props;
		return (
			<TouchableOpacity onPress={onPress}>
				<View style={styles.container}>
					<Text
					  style={StyleSheet.flatten([
					  	styles.text, 
					  	{
					  		textDecorationLine: completed ? 'line-through' : 'none',
					  		color: completed ? 'green' : 'red',
					  	}
					  ])} 
					>
						{text}
					</Text>
				</View>
			</TouchableOpacity>
		)
	}
}

const styles = StyleSheet.create({
	text: {
		fontSize: 24,
	},
	container: {
		borderBottomWidth: 1,
		borderBottomColor: 'black',
		marginHorizontal: 20,
		paddingVertical: 10,
	}
})

// todoList.js
import React, { Component } from 'react';
import {
	View,
	TouchableOpacity,
	StyleSheet,
	Dimensions
} from 'react-native';

import { connect } from 'react-redux';
import Todo from './todo';
import { toggleTodo } from '../redux/actions'

const {width, height} = Dimensions.get('window');

/**
 * The todo list 
 *
 * @reactProps {object} todos - an array of todos
 * @reactProps {function} onTodoClick - the action when user clicks on a to do item
 */
class TodoList extends Component {
	render() {
		let { todos, onTodoClick } = this.props
		return (
			<View style={styles.container}>
				{
					todos && todos.map(todo => 
						<Todo 
							key={todo.id}
							{...todo}
							onPress={() => onTodoClick(todo.id)}
						/>
					)
				}
			</View>
		)
	}
}

const mapStateToProps = (state) => {
	return {
		todos: state
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		onTodoClick: id => {
      dispatch(toggleTodo(id))
    }
	}
}


TodoList = connect(
	mapStateToProps,
	mapDispatchToProps)
(TodoList)

const styles = StyleSheet.create({
	container: {
		flex: 1,
		justifyContent: 'flex-start',
		width: width,
		//alignItems: 'center',
	}
})

export default TodoList

